import { StyleSheet } from 'react-native';
export const styles = StyleSheet.create({
    container: {

        flex: 1,
    
        backgroundColor: "#fff",
    
        alignItems: "center",
    
        justifyContent: "center",
    
      },
    
    inputView: {

        backgroundColor: "#d2b4de",
    
        borderRadius: 30,
    
        width: "100%",
    
        height: 65,
    
        marginBottom: 20,
    
        alignItems: "center",
    
      },
    
    TextInput: {
    
        height: 50,
    
        flex: 1,
    
        padding: 10,
    
        marginLeft: 20,
    
      },
    
    
    loginBtn: {
    
        width: "100%",
    
        borderRadius: 25,
    
        height: 50,
    
        alignItems: "center",
    
        justifyContent: "center",
    
        marginTop: 40,
    
        backgroundColor: "#a569bd",
    
      },
    
    });
